sealed class ConnectorException(message: String): kotlin.Exception(message) {
        
        class UnknownConnector(message: String) : ConnectorException(message)
        
        class UnsupportedFlow(message: String) : ConnectorException(message)
        
        class MissingAuthField(message: String) : ConnectorException(message)
        
        class MissingPaymentField(message: String) : ConnectorException(message)
        
        class TransformException(message: String) : ConnectorException(message)
        
        class ParseException(message: String) : ConnectorException(message)
        
        class InvalidInput(message: String) : ConnectorException(message)
        

    companion object ErrorHandler : UniffiRustCallStatusErrorHandler<ConnectorException> {
        override fun lift(error_buf: RustBuffer.ByValue): ConnectorException = FfiConverterTypeConnectorError.lift(error_buf)
    }
}

/**
 * @suppress
 */
public object FfiConverterTypeConnectorError : FfiConverterRustBuffer<ConnectorException> {
    override fun read(buf: ByteBuffer): ConnectorException {
        
            return when(buf.getInt()) {
            1 -> ConnectorException.UnknownConnector(FfiConverterString.read(buf))
            2 -> ConnectorException.UnsupportedFlow(FfiConverterString.read(buf))
            3 -> ConnectorException.MissingAuthField(FfiConverterString.read(buf))
            4 -> ConnectorException.MissingPaymentField(FfiConverterString.read(buf))
            5 -> ConnectorException.TransformException(FfiConverterString.read(buf))
            6 -> ConnectorException.ParseException(FfiConverterString.read(buf))
            7 -> ConnectorException.InvalidInput(FfiConverterString.read(buf))
            else -> throw RuntimeException("invalid error enum value, something is very wrong!!")
        }
        
    }

    override fun allocationSize(value: ConnectorException): ULong {
        return 4UL
    }

    override fun write(value: ConnectorException, buf: ByteBuffer) {
        when(value) {
            is ConnectorException.UnknownConnector -> {
                buf.putInt(1)
                Unit
            }
            is ConnectorException.UnsupportedFlow -> {
                buf.putInt(2)
                Unit
            }
            is ConnectorException.MissingAuthField -> {
                buf.putInt(3)
                Unit
            }
            is ConnectorException.MissingPaymentField -> {
                buf.putInt(4)
                Unit
            }
            is ConnectorException.TransformException -> {
                buf.putInt(5)
